mean(HbA1c_GLP1_BEFORE_vs_AFTER$HbA1c_After) #5.995803

median(HbA1c_GLP1_BEFORE_vs_AFTER$HbA1c_Prior) # 6.5
median(HbA1c_GLP1_BEFORE_vs_AFTER$HbA1c_After) # 5.8

